'use strict';

import React, { Component, } from 'react';
import { Text, TouchableOpacity, InteractionManager, Modal, Alert } from 'react-native';

import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import { View } from 'react-native-animatable';
import { Actions, } from 'react-native-router-flux';
import { Style, IconSize, Colors } from '../styles/style';
import I18n from '../i18n/translations';

import CompteSecureService from '../services/CompteSecureService';
import NoteDeFraisService from '../services/NoteDeFraisService';
import { STATUTS } from '../schemas/NoteDeFrais';

import NavBar from './NavBar';

const FILTRES = ['findAllDepenses', 'findAllIndemnitesKilometriquesWithRegulation', 'findAllAutresDepenses'];

/**
 * NavBar spécifique à la liste des dépenses communes
 *
 * @module app/components/DepenseCommuneNavBar.js
 * @override {NavBar}
 */
export default class DepenseCommuneNavBar extends NavBar {

  /**
   * Initialisation de l'état du composant.
   * Initialisation de l'ensemble des services utiles pour le chargement des données.
   */
  constructor (props) {
    super(props);

    /** @type {CompteSecureService} */
    this.compteSecureService = new CompteSecureService();
    /** @type {NoteDeFraisService} */
    this.noteDeFraisService = new NoteDeFraisService();
    /** @type {Object} */
    this.state = {
      filtre: FILTRES[0],
      showMenu: false,
    };

  }

  _getChecked (filtre) {
    if (this.state.filtre === filtre) {
      return <MaterialIcons name="check" size={IconSize.small} style={Colors.yellowGreen.color()}/>
    }
    return null
  }

  _renderMenu () {

    return FILTRES.map((filtre) => {
      return <TouchableOpacity key={filtre}
                               onPress={() => {
                                 this.setState({ filtre });
                                 InteractionManager.runAfterInteractions(() => {
                                   this.setState({ showMenu: false });
                                   Actions.refresh({ filtre: filtre });
                                 });
                               }}
                               value={filtre}
                               style={{ flex: 1, flexDirection: 'row' }}>
        <View style={{ flex: 1 }}>{this._getChecked(filtre)}</View>
        <Text style={{ fontSize: 18, margin: 2 }}>{I18n.t(`filters.${filtre}`)}</Text>
      </TouchableOpacity>

    })

  }

  _validateAfterConfirm () {
    try {
      const compteSecure = this.compteSecureService.getSelectedAccount();
      const noteDeFrais = this.noteDeFraisService.find(this.noteDeFraisService.findEnCours(compteSecure.compte));
      this.noteDeFraisService.validate(compteSecure.compte, noteDeFrais);

      let title = I18n.t('DepenseCommuneListe.afterValidate.title');
      let message = I18n.t('DepenseCommuneListe.afterValidate.message');

      Alert.alert(title, message, [{
        text: I18n.t('Alert.ok'), onPress: () => {
          Actions.chargement();
        }
      }]);

    } catch (e) {
      Alert.alert(I18n.t('Alert.warning'), e.message, [{ text: I18n.t('Alert.ok') }])
    }
  }

  /**
   * Méthode de validation d'une note de frais.
   */
  validate () {

    let title = I18n.t('DepenseCommuneListe.confirm.title');
    let message = I18n.t('DepenseCommuneListe.confirm.validationSimple');
    if (this.compteSecureService.shouldUseApiService()) {
      message = I18n.t('DepenseCommuneListe.confirm.validation');
    }

    Alert.alert(title, message,
        [
          { text: I18n.t('Alert.ok'), onPress: this._validateAfterConfirm.bind(this) },
          { text: I18n.t('Alert.cancel'), style: 'cancel' }
        ]
    );

  }

  /**
   * Méthode éxécuté lors du rendu du composant.
   *
   * @function render
   * @return react~Component
   */
  render () {

    if (this.hasSelected()) {
      return this.renderWhenSelected();
    }

    const validateAction = this.props.noteDeFrais.statut === STATUTS.inProgress.key ?
        <TouchableOpacity onPress={this.validate.bind(this)}>
          <MaterialIcons name="playlist-add-check" size={IconSize.medium} style={[Style.navBarIcon]}/>
        </TouchableOpacity> : null;

    const sum = I18n.toCurrency(this.props.noteDeFrais.totalDepenses + this.props.noteDeFrais.totalIndemnitesKilometriques);
    return <View style={Style.navBar}>

      <TouchableOpacity onPress={() => Actions.refresh({
        key: 'drawer',
        open: value => !value,
        onCloseStart: () => Actions.refresh({ selected: {} })
      })}>
        <MaterialIcons name="menu" size={IconSize.medium} style={[Style.navBarIcon]}/>
      </TouchableOpacity>

      <View style={[Style.flexRowCenter, Style.label]}>
        <Text style={[Style.labelText]}>{I18n.t(`state.${this.props.noteDeFrais.statut}`)}</Text>
      </View>

      <View style={[Style.flexRowCenterEnd, Style.label, Style.success]}>
        <Text style={[Style.labelText]}>{sum}</Text>
      </View>


      <TouchableOpacity onPress={() => {
        this.setState({ showMenu: !this.state.showMenu })
      }}>
        <MaterialIcons name="filter-list" size={IconSize.medium} style={[Style.navBarIcon]}/>
        <Modal animationType={"fade"}
               transparent={true}
               visible={this.state.showMenu}
               onRequestClose={() => {
                 this.setState({ showMenu: !this.state.showMenu })
               }}>

          <View style={{
            backgroundColor: 'white',
            width: 220,
            position: 'absolute',
            right: 10,
            top: 20,
            flex: 1,
            margin: 10,
            padding: 10,
            borderRadius: 5,
            borderWidth: 1,
            borderColor: Colors.greyLighter.code
          }}>
            {this._renderMenu()}
          </View>
        </Modal>

      </TouchableOpacity>

      {validateAction}

    </View>
  }

}

'use strict';

import React, { Component, } from 'react';

import { Text, TouchableOpacity } from 'react-native';

import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import { View } from 'react-native-animatable';
import { Actions, } from 'react-native-router-flux';
import { Style, IconSize, Colors } from '../styles/style';

import CategorieDepenseService from '../services/CategorieDepenseService';
import CompteSecureService from '../services/CompteSecureService';
import DepenseService from '../services/DepenseService';
import IndemniteKilometriqueService from '../services/IndemniteKilometriqueService';
import NoteDeFraisService from '../services/NoteDeFraisService';
import VehiculeService from '../services/VehiculeService';

/**
 * NavBar spécifique à l'application
 *
 * @module app/components/NavBar.js
 * @override React~Component
 */
export default class NavBar extends Component {

  /**
   * Initialisation de l'état du composant.
   * Initialisation de l'ensemble des services utiles pour le chargement des données.
   */
  constructor () {
    super();
    /** @type {CategorieDepenseService} */
    this.categorieDepenseService = new CategorieDepenseService();
    /** @type {CompteSecureService} */
    this.compteSecureService = new CompteSecureService();
    /** @type {DepenseService} */
    this.depenseService = new DepenseService();
    /** @type {IndemniteKilometriqueService} */
    this.indemniteKilometriqueService = new IndemniteKilometriqueService();
    /** @type {NoteDeFraisService} */
    this.noteDeFraisService = new NoteDeFraisService();
    /** @type {VehiculeService} */
    this.vehiculeService = new VehiculeService();
  }

  /**
   * Méthode qui retourne le fait de pouvoir supprimer
   * @param props
   * @returns {boolean} true
   */
  shouldDelete (props) {
    return true;
  }

  /**
   * Méthode qui supprime l'ensemble des éléments sélectionnés
   * @param selected
   */
  deleteAll (selected) {
    const ndf = this.props.noteDeFrais;
    const all = Object.keys(selected);

    if (ndf) {
      this.depenseService.deleteAll(all, ndf);
      this.indemniteKilometriqueService.deleteAll(all, ndf);
      this.noteDeFraisService.updateTotal(ndf);
    }

    this.vehiculeService.deleteAll(all);
    this.categorieDepenseService.deleteAll(all);

    Actions.refresh({ selected: {} });
  }

  /**
   * Retour le titre de la NavBar
   * @returns {*}
   */
  getTitle () {
    return this.props.title;
  }

  /**
   * Affirme la présence d'un menu
   * @returns {boolean}
   */
  hasMenu () {
    return true;
  }

  /**
   * Affirme si des éléments sont sélectionnés
   * @returns {boolean}
   */
  hasSelected () {
    return this.props.selected && Object.values(this.props.selected).filter((value)=> value === true).length > 0;
  }

  /**
   * Rendu graphique lorsque des éléments sont sélectionés
   * @returns React~Component
   */
  renderWhenSelected () {

    const hasSelected = this.hasSelected();

    const deleteIcon = this.shouldDelete(this.props) && hasSelected ?
        <TouchableOpacity onPress={this.deleteAll.bind(this, this.props.selected)}>
          <MaterialIcons name="delete" size={IconSize.medium} style={Style.navBarIcon}/>
        </TouchableOpacity> : null;

    const menuAction = this.hasMenu() ?
        <TouchableOpacity onPress={()=> Actions.refresh({
          key: 'drawer',
          open: value => !value,
          onCloseStart: ()=> Actions.refresh({ selected: {} })
        })}>
          <MaterialIcons name="menu" size={IconSize.medium} style={[Style.navBarIcon]}/>
        </TouchableOpacity>
        : <TouchableOpacity onPress={()=> {
      if (hasSelected) {
        Actions.refresh({ selected: {} })
      }
      else {
        Actions.pop();
      }
    }}>
      <MaterialIcons name="arrow-back" size={IconSize.medium} style={Colors.white.color()}/>
    </TouchableOpacity>;

    const title = hasSelected ? Object.keys(this.props.selected).length : this.getTitle();

    const backgroundColor = hasSelected ? Colors.greyDarker.background() : null;

    return <View style={[Style.navBar, backgroundColor]}>

      {menuAction}

      <Text style={[Style.navBarTitle, { flex: 1 }]}>{title}</Text>

      {deleteIcon}
    </View>
  }

  /**
   * Méthode éxécuté lors du rendu du composant.
   *
   * @return react~Component
   */
  render () {

    if (this.hasSelected()) {
      return this.renderWhenSelected();
    }

    return <View style={Style.navBar}>

      <TouchableOpacity onPress={()=> Actions.refresh({
        key: 'drawer',
        open: value => !value,
        onCloseStart: ()=> Actions.refresh({ selected: {} })
      })}>
        <MaterialIcons name="menu" size={IconSize.medium} style={[Style.navBarIcon]}/>
      </TouchableOpacity>

      <View style={[Style.flexRowCenter, Style.label]}>
        <Text style={[Style.labelText]}>{this.getTitle()}</Text>
      </View>


    </View>
  }

}

import React from 'react';
import { View, Text, Switch } from 'react-native';

import { Style } from '../styles/style';

function checkbox (locals) {
  if (locals.hidden) {
    return null;
  }
  return (
      <View style={[Style.formRow, Style.inline]}>

        <Text style={Style.input}>{locals.label}</Text>

        <Switch
            accessibilityLabel={locals.label}
            ref="input"
            disabled={locals.disabled}
            onTintColor={locals.onTintColor}
            thumbTintColor={locals.thumbTintColor}
            tintColor={locals.tintColor}
            onValueChange={(value) => locals.onChange(value)}
            value={locals.value}
        />

      </View>
  );
}

module.exports = checkbox;

import React from 'react';

const t = require('tcomb-form-native');
const Component = t.form.Component;
const Nil = t.Nil;
import DatepickerField from './datepicker-field';
import I18n from '../i18n/translations';
import moment from 'moment';

export default class DatePicker extends Component {

  getTemplate () {
    return DatepickerField;
  }

  getLocals () {
    const locals = super.getLocals();
    [
      'help',
      'maximumDate',
      'minimumDate',
      'minuteInterval',
      'mode',
      'timeZoneOffsetInMinutes',
      'icon',
      'iconEnd',
      'onSubmitEditing',
      'format'
    ].forEach((name) => locals[name] = this.props.options[name]);

    return locals;
  }

}

DatePicker.transformer = {
  format: value => Nil.is(value) ? new Date() : value,
  parse: value => {
    return moment(value, I18n.t('dateFormat')).toDate();
  }
};

import React, { PropTypes } from 'react';
import { Animated, View, TouchableOpacity, Text } from 'react-native';
import { Style } from '../styles/style';
import DatePicker from './datepicker';
import I18n from '../i18n/translations';

function datepicker (locals) {
  if (locals.hidden) {
    return null;
  }

  const stylesheet = locals.stylesheet;
  let formGroupStyle = stylesheet.formGroup.normal;
  let controlLabelStyle = stylesheet.controlLabel.normal;
  let helpBlockStyle = stylesheet.helpBlock.normal;
  const errorBlockStyle = stylesheet.errorBlock;

  if (locals.hasError) {
    formGroupStyle = stylesheet.formGroup.error;
    controlLabelStyle = stylesheet.controlLabel.error;
    helpBlockStyle = stylesheet.helpBlock.error;
  }

  const label = locals.label ? <Text style={controlLabelStyle}>{locals.label}</Text> : null;
  const help = locals.help ? <Text style={helpBlockStyle}>{locals.help}</Text> : null;
  const error = locals.hasError && locals.error ?
      <Text accessibilityLiveRegion="polite" style={errorBlockStyle}>{locals.error}</Text> : null;

  return (
      <View style={[Style.formRow, Style.inline, formGroupStyle]}>
        {label}
        <DatePicker
            ref="datepicker"
            style={[Style.inline]}
            date={locals.value}
            mode="date"
            placeholder={locals.placeholder}
            format={locals.format}
            confirmBtnText={I18n.t('datepicker.confirmBtnText')}
            cancelBtnText={I18n.t('datepicker.cancelBtnText')}
            showIcon={true}
            onDateChange={(value) => locals.onChange(value)}
            onSubmitEditing={locals.onSubmitEditing}/>
        {help}
        {error}
      </View>
  );
}

module.exports = datepicker;

import React, { Component } from 'react';
import {
    View,
    Text,
    Image,
    Modal,
    TouchableHighlight,
    DatePickerAndroid,
    TimePickerAndroid,
    DatePickerIOS,
    Platform,
    Animated
} from 'react-native';
import DatepickerStyle from '../styles/datepicker-style';
import moment from 'moment';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons'
import { IconSize,Style, Colors } from '../styles/style';

const FORMATS = {
  'date': 'YYYY-MM-DD',
  'datetime': 'YYYY-MM-DD HH:mm',
  'time': 'HH:mm'
};

class DatePicker extends Component {

  /**
   * Initialisation de l'état du composant.
   * Initialisation de l'ensemble des services utiles pour le chargement des données.
   */
  constructor (props) {
    super(props);

    this.format = this.props.format || FORMATS[this.props.mode];

    this.state = {
      date: this.getDate(),
      modalVisible: false,
      disabled: this.props.disabled,
      animatedHeight: new Animated.Value(0)
    };

    this.datePicked = this.datePicked.bind(this);
    this.submitEditing = this.submitEditing.bind(this);
    this.onPressDate = this.onPressDate.bind(this);
    this.onPressCancel = this.onPressCancel.bind(this);
    this.onPressConfirm = this.onPressConfirm.bind(this);
    this.onDatePicked = this.onDatePicked.bind(this);
    this.onTimePicked = this.onTimePicked.bind(this);
    this.onDatetimePicked = this.onDatetimePicked.bind(this);
    this.onDatetimeTimePicked = this.onDatetimeTimePicked.bind(this);
    this.setModalVisible = this.setModalVisible.bind(this);
  }

  componentWillMount () {
    // ignore the warning of Failed propType for date of DatePickerIOS, will remove after being fixed by official
    console.ignoredYellowBox = [
      'Warning: Failed propType'
      // Other warnings you don't want like 'jsSchedulingOverhead',
    ];
  }

  setModalVisible (visible) {
    this.setState({ modalVisible: visible });

    // slide animation
    if (visible) {
      Animated.timing(
          this.state.animatedHeight,
          {
            toValue: this.props.height,
            duration: this.props.duration
          }
      ).start();
    }
    else {
      this.setState({
        animatedHeight: new Animated.Value(0)
      });
    }
  }

  onPressCancel () {
    this.setModalVisible(false);
  }

  onPressConfirm () {
    this.datePicked();
    this.setModalVisible(false);
    this.submitEditing();
  }

  getDate (date = this.props.date) {
    if (!date) {
      let now = new Date();
      if (this.props.minDate) {
        let minDate = this.getDate(this.props.minDate);

        if (now < minDate) {
          return minDate;
        }
      }

      if (this.props.maxDate) {
        let maxDate = this.getDate(this.props.maxDate);

        if (now > maxDate) {
          return maxDate;
        }
      }

      return now;
    }

    if (date instanceof Date) {
      return date;
    }

    return moment(date, this.format).toDate();
  }

  getDateStr (date = this.props.date) {
    if (date instanceof Date) {
      return moment(date).format(this.format);
    }
    else {
      return moment(this.getDate(date)).format(this.format);
    }
  }

  datePicked () {
    if (typeof this.props.onDateChange === 'function') {
      this.props.onDateChange(this.getDateStr(this.state.date), this.state.date);
    }
  }

  submitEditing () {
    if (typeof this.props.onSubmitEditing === 'function') {
      // Waiting modal...
      setTimeout(()=> {
        this.props.onSubmitEditing();
      }, this.props.duration);

    }
  }

  getTitleElement () {
    const { date, placeholder } = this.props;
    if (!date && placeholder) {
      return (<Text style={[DatepickerStyle.placeholderText, this.props.customStyles.placeholderText]}>{placeholder}</Text>);
    }
    return (<Text style={[DatepickerStyle.dateText, this.props.customStyles.dateText]}>{this.getDateStr()}</Text>);
  }

  onDatePicked ({ action, year, month, day }) {
    if (action !== DatePickerAndroid.dismissedAction) {
      this.setState({
        date: new Date(year, month, day)
      });
      this.datePicked();
    }
  }

  onTimePicked ({ action, hour, minute }) {
    if (action !== DatePickerAndroid.dismissedAction) {
      this.setState({
        date: moment().hour(hour).minute(minute).toDate()
      });
      this.datePicked();
    }
  }

  onDatetimePicked ({ action, year, month, day }) {
    if (action !== DatePickerAndroid.dismissedAction) {
      let timeMoment = moment(this.state.date);

      TimePickerAndroid.open({
        hour: timeMoment.hour(),
        minute: timeMoment.minutes(),
        is24Hour: !this.format.match(/h|a/)
      }).then(this.onDatetimeTimePicked.bind(this, year, month, day));
    }
  }

  onDatetimeTimePicked (year, month, day, { action, hour, minute }) {
    if (action !== DatePickerAndroid.dismissedAction) {
      this.setState({
        date: new Date(year, month, day, hour, minute)
      });
      this.datePicked();
    }
  }

  onPressDate () {
    if (this.state.disabled) {
      return true;
    }

    // reset state
    this.setState({
      date: this.getDate()
    });

    if (Platform.OS === 'ios') {
      this.setModalVisible(true);
    }
    else {

      if (this.props.mode === 'date') {
        DatePickerAndroid.open({
          date: this.state.date,
          minDate: this.props.minDate && this.getDate(this.props.minDate),
          maxDate: this.props.maxDate && this.getDate(this.props.maxDate)
        }).then(this.onDatePicked);
      }
      else if (this.props.mode === 'time') {

        let timeMoment = moment(this.state.date);

        TimePickerAndroid.open({
          hour: timeMoment.hour(),
          minute: timeMoment.minutes(),
          is24Hour: !this.format.match(/h|a/)
        }).then(this.onTimePicked);
      }
      else if (this.props.mode === 'datetime') {

        DatePickerAndroid.open({
          date: this.state.date,
          minDate: this.props.minDate && this.getDate(this.props.minDate),
          maxDate: this.props.maxDate && this.getDate(this.props.maxDate)
        }).then(this.onDatetimePicked);
      }
      else {
        throw new Error('The specified mode is not supported');
      }
    }
  }

  /**
   * Méthode éxécuté lors du rendu du composant.
   *
   * @function render
   * @return react~Component
   */
  render () {
    let customStyles = this.props.customStyles;
    this.format = this.props.format || FORMATS[this.props.mode];
    const dateInputStyle = [
      DatepickerStyle.dateInput, customStyles.dateInput,
      this.state.disabled && DatepickerStyle.disabled,
      this.state.disabled && customStyles.disabled
    ];

    return (
        <TouchableHighlight
            ref="button"
            style={[DatepickerStyle.dateTouch, this.props.style]}
            underlayColor={'transparent'}
            onPress={this.onPressDate}>

          <View style={[DatepickerStyle.dateTouchBody, customStyles.dateTouchBody, {flex:1}]}>

            {this.props.showIcon &&


            <MaterialIcons name="date-range" size={IconSize.medium}
                           style={[Colors.greyDarker.color(), { marginRight: 5 }]}/>
            }

            <Text style={Style.input}>
              {this.getTitleElement()}
            </Text>

            {this.props.showIcon &&


            <MaterialIcons name="arrow-drop-down" size={IconSize.medium}
                           style={[Colors.greyDarker.color(), { marginRight: 5 }]}/>
            }


            {Platform.OS === 'ios' && <Modal
                transparent={true}
                visible={this.state.modalVisible}
                onRequestClose={() => {
                  this.setModalVisible(false);
                }}>
              <TouchableHighlight
                  style={DatepickerStyle.datePickerMask}
                  activeOpacity={1}
                  underlayColor={'#00000077'}
                  onPress={this.onPressCancel}>
                <TouchableHighlight
                    underlayColor={'#fff'}
                    style={{ flex: 1 }}>
                  <Animated.View
                      style={[DatepickerStyle.datePickerCon, { height: this.state.animatedHeight }, customStyles.datePickerCon]}>
                    <DatePickerIOS
                        date={this.state.date}
                        mode={this.props.mode}
                        minimumDate={this.props.minDate && this.getDate(this.props.minDate)}
                        maximumDate={this.props.maxDate && this.getDate(this.props.maxDate)}
                        onDateChange={(date) => this.setState({ date: date })}
                        style={[DatepickerStyle.datePicker, customStyles.datePicker]}/>
                    <TouchableHighlight
                        underlayColor={'transparent'}
                        onPress={this.onPressCancel}
                        style={[DatepickerStyle.btnText, DatepickerStyle.btnCancel, customStyles.btnCancel]}>
                      <Text
                          style={[DatepickerStyle.btnTextText, DatepickerStyle.btnTextCancel, customStyles.btnTextCancel]}>
                        {this.props.cancelBtnText}
                      </Text>
                    </TouchableHighlight>
                    <TouchableHighlight
                        underlayColor={'transparent'}
                        onPress={this.onPressConfirm}
                        style={[DatepickerStyle.btnText, DatepickerStyle.btnConfirm, customStyles.btnConfirm]}
                    >
                      <Text style={[DatepickerStyle.btnTextText, customStyles.btnTextConfirm]}>{this.props.confirmBtnText}</Text>
                    </TouchableHighlight>
                  </Animated.View>
                </TouchableHighlight>
              </TouchableHighlight>
            </Modal>}
          </View>
        </TouchableHighlight>
    );
  }
}

DatePicker.defaultProps = {
  mode: 'date',
  date: '',
  // component height: 216(DatePickerIOS) + 1(borderTop) + 42(marginTop), IOS only
  height: 259,

  // slide animation duration time, default to 300ms, IOS only
  duration: 300,
  confirmBtnText: '确定',
  cancelBtnText: '取消',
  customStyles: {},

  // whether or not show the icon
  showIcon: false,
  disabled: false,
  placeholder: ''
};

DatePicker.propTypes = {
  mode: React.PropTypes.oneOf(['date', 'datetime', 'time']),
  date: React.PropTypes.oneOfType([React.PropTypes.string, React.PropTypes.instanceOf(Date)]),
  minDate: React.PropTypes.oneOfType([React.PropTypes.string, React.PropTypes.instanceOf(Date)]),
  maxDate: React.PropTypes.oneOfType([React.PropTypes.string, React.PropTypes.instanceOf(Date)]),
  height: React.PropTypes.number,
  duration: React.PropTypes.number,
  confirmBtnText: React.PropTypes.string,
  cancelBtnText: React.PropTypes.string,
