'use strict';

import React, {
  Component,
} from 'react';

import {
  Alert,
  Platform,
  Text,
  TextInput,
  View,
  ScrollView,
  findNodeHandle,
  TouchableOpacity,
  InteractionManager
} from 'react-native';

import MaterialIcons from 'react-native-vector-icons/MaterialIcons';

import { Style, IconSize } from '../styles/style';
import FormStyle from '../styles/formStyle';
import { Actions, } from 'react-native-router-flux';
import GoogleAnalytics from 'react-native-google-analytics-bridge';
import PlatformHelper from '../lib/platform-helper';

import utils from '../lib/utils'

const t = require('tcomb-form-native');
const Form = t.form.Form;

import _ from 'underscore';

let currentForm;

/**
 * Composant permetant d'automatiser la création de formulaire basée sur les modèles de données realm.
 * @module app/components/RealmForm.js
 * @override React.Component
 */
export default class RealmForm extends Component {

  _initForm (value) {
    const fields = utils.toFieldsOptions(this.getConfig(), this.getActions());

    this.setState({
      value: value,
      options: {
        auto: 'placeholders',
        i18n: {
          optional: '',
          required: '',
          add: 'Add',   // add button
          remove: '✘',  // remove button
          up: '↑',      // move up button
          down: '↓'     // move down button
        },
        stylesheet: FormStyle,
        fields
      },
      form: utils.toForm(this.getConfig())
    });

  }

  /**
   * Initialisation de l'état du composant.
   * Initialisation de l'ensemble des services utiles pour le chargement des données.
   */
  constructor (props) {
    super(props);
    this.state = {
      value: null,
      options: {
        auto: 'placeholders',
        i18n: {
          optional: '',
          required: '',
          add: 'Add',   // add button
          remove: '✘',  // remove button
          up: '↑',      // move up button
          down: '↓'     // move down button
        },
        stylesheet: FormStyle,
        fields: null
      },
      form: null
    };
  }

  componentDidMount () {
    InteractionManager.runAfterInteractions(() => {
      GoogleAnalytics.trackScreenView(`Formulaire ${this.getConfig().schema.name}`);
      this._initForm(this.getStateValue());
      currentForm = this.refs.form;
      this._getInput().refs.input.focus && this._getInput().refs.input.focus();
    });
  }

  componentWillReceiveProps (props) {
    this._initForm(this.state.value);
    if (props.place && props.place.data.description !== this.state.value[props.place.input]) {
      const value = _.clone(this.state.value);
      value[props.place.input] = props.place.data.description;
      this.setState({ value });
    }

    else if (props.distance && props.distance.data !== this.state.value[props.distance.input]) {
      const value = _.clone(this.state.value);
      value[props.distance.input] = props.distance.data;
      this.setState({ value });
    }
    else if (props.valeurAnalytique) {
      const value = _.clone(this.state.value);
      const valeursAnalytiques = _.toArray(value.valeursAnalytiques);
      const index = _.findLastIndex(valeursAnalytiques, { idAxeAnalytique: props.valeurAnalytique.idAxeAnalytique });
      if (index > -1) {
        valeursAnalytiques[index] = props.valeurAnalytique;
      }
      else {
        valeursAnalytiques.push(props.valeurAnalytique);
      }

      value.valeursAnalytiques = valeursAnalytiques;
      this.setState({ value });
    }

  }

  getConfig () {
  }

  /**
   * Méthode qui retourne la valeur de l'état du formulaire
   * @returns {Object}
   */
  getStateValue () {
  }

  _getInput (index = 0) {
    if (this.state && this.state.options.fields) {
      const fields = Object.keys(this.state.options.fields);
      const fieldId = fields[index];
      if (fieldId) {
        const input = this.refs.form.getComponent(fieldId);
        if (input && input.refs) {
          if (input.refs.input && input.refs.input.props.editable !== false) {
            return input
          }
          else if (input.refs.datepicker) {
            return input;
          }
        }
        return this._getInput(index + 1);
      }

    }
    return null;
  }

  getActions () {
    return {
      onSubmitEditing: this._focusNextInput.bind(this),
      onFocus: this._scrollToInput.bind(this),
      onCallApi: this._onCallApi.bind(this)
    };
  }

  _onCallApi (inputId, apiId, param) {
    if (apiId === 'axesAnalytiques') {
      const valeurAnalytique = _.findWhere(_.toArray(this.state.value.valeursAnalytiques), { idAxeAnalytique: param.id });
      const value = valeurAnalytique ? valeurAnalytique.description : '';
      Actions.valeursAnalytiquesAutocomplete({ input: inputId, axe: param, value });
    }
  }

  _focusNextInput (inputId) {
    const fields = Object.keys(this.state.options.fields);
    const currentInput = fields.indexOf(inputId);
    if (currentInput !== -1 && fields.length >= currentInput + 1) {
      const nextInpuId = fields[currentInput + 1];
      if (nextInpuId) {
        if (this.state.options.fields[nextInpuId].editable === false) {
          return this._focusNextInput(nextInpuId);
        }
        const nextInput = this.refs.form.getComponent(nextInpuId);
        if (nextInput) {
          if (nextInput.refs.input) {
            nextInput.refs.input.focus();
          }
          else if (nextInput.refs.datepicker) {
            nextInput.refs.datepicker.onPressDate();
          }
        }
      }
    }
  }

  _scrollToInput (inputId) {
    if (!PlatformHelper.isAndroid()) {
      setTimeout(() => {
        if (this.refs.scrollView) {
          const scrollResponder = this.refs.scrollView.getScrollResponder();
          let input = this.refs.form.getComponent(inputId);
          if (!input) {
            input = this._getInput();
          }
          const nodle = findNodeHandle(input);
          scrollResponder.scrollResponderScrollNativeHandleToKeyboard(nodle, 110, true);
        }
      }, 50);
    }

  }

  componentDidUpdate () {
    currentForm = this.refs.form;
  }

  onChange (value) {
    this.setState({ value });
  }

  static _save (props) {

    const validate = currentForm.validate();

    if (validate.errors.length > 0) {
      console.log(validate.errors);
    }
    else {
      let values = {};
      try {
        values = _.extend(values, currentForm.getValue());
        this.save(props, values);
      } catch (e) {
        console.log('--Erreur de saisie', values);
        Alert.alert('Erreur de saisie', e.message,
            [
              { text: 'Ok', onPress: () => console.log('Cancel Pressed'), style: 'cancel' },
            ]
        )

      }
    }
  }

  static _delete (props) {
    try {
      this.delete(currentForm.getValue(), props);
    } catch (e) {
      Actions.pop();
    }
  }

  static getTitle () {
    return 'Realm form...';
  }

  static shouldDelete (props) {
    return true;
  }

  static hasMenu () {
    return false;
  }

  static renderNavigationBar (props) {

    const deleteAction = this.shouldDelete(props) ? <TouchableOpacity onPress={this._delete.bind(this, props)}>
      <MaterialIcons name="delete" size={IconSize.medium} style={Style.navBarIcon}/>
    </TouchableOpacity> : null;

    const menuAction = this.hasMenu() ?
        <TouchableOpacity onPress={() => Actions.refresh({
          key: 'drawer',
          open: value => !value,
          onCloseStart: () => Actions.refresh({ selected: {} })
        })}>
          <MaterialIcons name="menu" size={IconSize.medium} style={[Style.navBarIcon]}/>
        </TouchableOpacity>
        :
        <TouchableOpacity onPress={() => Actions.pop({ account: props.account })}>
          <MaterialIcons name="arrow-back" size={IconSize.medium} style={Style.navBarIcon}/>
        </TouchableOpacity>;

    return <View style={Style.navBar}>

      <View style={[Style.flexRowCenter]}>

        {menuAction}

        <Text style={[Style.navBarTitle]}>{this.getTitle(props)}</Text>

      </View>

      {deleteAction}

      <TouchableOpacity onPress={this._save.bind(this, props)}>
        <MaterialIcons name="check" size={IconSize.medium} style={Style.navBarIcon}/>
      </TouchableOpacity>

    </View>
  }

  /**
   * Méthode éxécuté lors du rendu du composant.
   *
   * @function render
   * @return react~Component
   */
  render () {
    if (this.state.form && this.state.options) {
      return <View style={Style.containerWithNavBar}>

        <ScrollView ref='scrollView' scrollEnabled={false} showsVerticalScrollIndicator={true}>

          <Form ref="form" type={this.state.form} options={this.state.options} value={this.state.value}
                onChange={this.onChange.bind(this)} style={{ backgroundColor: 'red' }}/>

        </ScrollView>
      </View>
    }
    return null;
  }

}

import React from 'react';

const t = require('tcomb-form-native');
const Component = t.form.Component;
const Nil = t.Nil;

import _ from 'underscore';

import SelectField from './select-field';

function sortByText (a, b) {
  return a.text < b.text ? -1 : a.text > b.text ? 1 : 0;
}

function getComparator (order) {
  return {
    asc: sortByText,
    desc: (a, b) => -sortByText(a, b)
  }[order];
}

export default class SelectFieldFactory extends Component {

  getTransformer () {
    const options = this.props.options;
    if (options.transformer) {
      return options.transformer;
    }
    return SelectFieldFactory.transformer(this.getNullOption());
  }

  getTemplate () {
    return SelectField;
  }

  getNullOption () {
    return this.props.options.nullOption || { value: '', text: '-' };
  }

  getEnum () {
    return this.typeInfo.innerType;
  }

  getPlaceholder () {
    let placeholder = this.props.options.placeholder;
    if (Nil.is(placeholder) && this.getAuto() === 'placeholders') {
      placeholder = this.getDefaultLabel();
    }
    return placeholder;
  }

  onChange (value, valie) {
    this.setState({ value }, () => {
      this.props.onChange(value, this.props.ctx.path)
    });
  }

  getOptions () {
    const options = this.props.options;
    const items = options.options ? options.options.slice() : getOptionsOfEnum(this.getEnum());
    if (options.order) {
      items.sort(getComparator(options.order));
    }
    const nullOption = this.getNullOption();
    if (options.nullOption !== false) {
      items.unshift(nullOption);
    }
    return items;
  }

  getLocals () {
    const locals = super.getLocals();
    locals.options = this.getOptions();
    locals.placeholder = this.getPlaceholder();
    locals.onChange = this.onChange.bind(this);

    [
      'help',
      'enabled',
      'mode',
      'prompt',
      'itemStyle',
      'onSubmitEditing',
      'icon'
    ].forEach((name) => locals[name] = this.props.options[name]);

    return locals;
  }

}

SelectFieldFactory.transformer = (nullOption) => {
  return {
    format: value => {

      if (typeof value === 'object') {
        return _.toArray(value);
      }

      return Nil.is(value) && nullOption ? nullOption.value : String(value)
    },
    parse: value => {
      if (typeof value === 'object') {
        return value
      }
      return nullOption && nullOption.value === value ? null : value
    }
  };
};

import React, { Component } from 'react';
import { View } from 'react-native';
import { Style } from '../styles/style';
import Select from './select';
import I18n from '../i18n/translations';

function select (locals) {
  if (locals.hidden) {
    return null;
  }

  let stylesheet = locals.stylesheet;
  let textboxStyle = stylesheet.select.normal;

  if (locals.hasError) {
    textboxStyle = stylesheet.select.error;
  }

  if (locals.editable === false) {
    textboxStyle = stylesheet.select.notEditable;
  }

  const onChange = (index, id)=> {
    locals.value = locals.value || [];
    locals.value[index - 1] = { id: id };
    locals.onChange(locals.value);
  };

  if (locals.options && locals.options.length > 1) {
    if (Array.isArray(locals.options[1])) {

      return <View ref='input-view'>
        {
          locals.options.map((options, index)=> {

            if (Array.isArray(options)) {
              return (<Select
                  ref={`input-${index}`}
                  key={`input-${index}`}
                  index={index}
                  value={locals.value[index - 1] ? locals.value[index - 1].id : ''}
                  options={[locals.options[0]].concat(options)}
                  onValuePicked={onChange.bind(this, index)}
                  placeholder={locals.placeholder}
                  style={[Style.formRow, Style.inline]}
                  icon={locals.icon}
                  textboxStyle={textboxStyle}
                  onSubmitEditing={locals.onSubmitEditing}
                  confirmBtnText={I18n.t('datepicker.confirmBtnText')}
                  cancelBtnText={I18n.t('datepicker.cancelBtnText')}
              />)
            }
            return null;
          })
        }
      </View>

    }

  }

  return (
      <Select
          ref="input"
          value={locals.value}
          options={locals.options}
          onValuePicked={locals.onChange}
          placeholder={locals.placeholder}
          style={[Style.formRow, Style.inline]}
          icon={locals.icon}
          textboxStyle={textboxStyle}
          onSubmitEditing={locals.onSubmitEditing}
          confirmBtnText={I18n.t('datepicker.confirmBtnText')}
          cancelBtnText={I18n.t('datepicker.cancelBtnText')}
      />
  );

}

module.exports = select;

import React, { Component } from 'react';
import {
  View,
  Text,
  Image,
  Modal,
  TouchableHighlight,
  Platform,
  Animated,
  Picker
} from 'react-native';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons'
import { IconSize, Style, Colors } from '../styles/style';
import DatepickerStyle from '../styles/datepicker-style';
import PlatformHelper from '../lib/platform-helper';

class Select extends Component {

  /**
   * Initialisation de l'état du composant.
   * Initialisation de l'ensemble des services utiles pour le chargement des données.
   */
  constructor (props) {
    super(props);
    this.state = {
      value: this.props.value,
      modalVisible: false,
      disabled: this.props.disabled,
      animatedHeight: new Animated.Value(0)
    };

    this.valuePicked = this.valuePicked.bind(this);
    this.submitEditing = this.submitEditing.bind(this);
    this.onPressValue = this.onPressValue.bind(this);
    this.onPressCancel = this.onPressCancel.bind(this);
    this.onPressConfirm = this.onPressConfirm.bind(this);
    this.onValuePicked = this.onValuePicked.bind(this);
    this.setModalVisible = this.setModalVisible.bind(this);
  }

  componentWillMount () {
    // ignore the warning of Failed propType for date of DatePickerIOS, will remove after being fixed by official
    console.ignoredYellowBox = [
      'Warning: Failed propType'
      // Other warnings you don't want like 'jsSchedulingOverhead',
    ];
  }

  setModalVisible (visible) {
    this.setState({ modalVisible: visible });

    // slide animation
    if (visible) {
      Animated.timing(
          this.state.animatedHeight,
          {
            toValue: this.props.height,
            duration: this.props.duration
          }
      ).start();
    }
    else {
      this.setState({
        animatedHeight: new Animated.Value(0)
      });
    }
  }

  onPressCancel () {
    this.setModalVisible(false);
  }

  onPressConfirm () {
    this.valuePicked();
    this.setModalVisible(false);
    this.submitEditing();
  }

  valuePicked () {
    if (typeof this.props.onValuePicked === 'function') {
      this.props.onValuePicked(this.state.value);
    }
  }

  focus () {
    this.onPressValue();
  }

  submitEditing () {
    if (typeof this.props.onSubmitEditing === 'function') {
      setTimeout(() => {
        this.props.onSubmitEditing();
      }, this.props.duration);
    }
  }

  getTitleElement () {
    const { value, placeholder } = this.props;
    if (!value && placeholder) {
      return <Text style={[Style.input, Style.placeholder, this.props.textboxStyle]}>{placeholder}</Text>
    }
    return <Text style={[Style.input, this.props.textboxStyle]}>{this._getText(value)}</Text>;
  }

  _getText (value) {
    return this.props.options.filter((o) => o.value === value).map((o) => o.text)[0];
  }

  onValuePicked ({ value }) {
    this.setState({
      value
    });
    this.valuePicked();
  }

  onPressValue () {
    if (this.state.disabled) {
      return true;
    }

    if (Platform.OS === 'ios') {
      this.setModalVisible(true);
    }

  }

  getPicker () {
    const options = this.props.options.map(({ value, text }) => <Picker.Item key={value} value={value} label={text}/>);

    const onValueChange = (value) => {
      this.setState({ value });

      if (PlatformHelper.isAndroid()) {
        if (typeof this.props.onValuePicked === 'function') {
          this.props.onValuePicked(value);
        }
        this.submitEditing();
      }
    };

    return <Picker
        style={[Style.select, this.props.textboxStyle]}
        mode='dropdown'
        selectedValue={this.state.value}
        onValueChange={(value) => onValueChange(value)}>
      {options}
    </Picker>

  }

  /**
   * Méthode éxécuté lors du rendu du composant.
   *
   * @function render
   * @return react~Component
   */
  render () {

    let customStyles = this.props.customStyles;
    if (PlatformHelper.isAndroid()) {
      return <TouchableHighlight
          ref="button"
          style={[this.props.style]}
          underlayColor={'transparent'}
          onPress={this.onPressValue.bind(this)}>

        <View style={[Style.formRow, Style.inline, formGroupStyle]}>

          { this.props.icon && <MaterialIcons name={this.props.icon}
                                              size={IconSize.medium}
                                              style={[Colors.greyDarker.color()]}/> }

          {this.getPicker()}

          <MaterialIcons name="arrow-drop-down" size={IconSize.medium}
                         style={[Colors.greyDarker.color(), { marginRight: 5 }]}/>

        </View>

      </TouchableHighlight>
    }

    return (<TouchableHighlight
            ref="button"
            style={[this.props.style]}
            underlayColor={'transparent'}
            onPress={this.onPressValue.bind(this)}>

          <View style={{ flex: 1, flexDirection: 'row', alignItems: 'center', justifyContent: 'center' }}>

            { this.props.icon && <MaterialIcons name={this.props.icon} size={IconSize.medium}
                                                style={[Colors.greyDarker.color(), { marginRight: 5 }]}/> }

            {this.getTitleElement()}

            <MaterialIcons name="arrow-drop-down" size={IconSize.medium}
                           style={[Colors.greyDarker.color(), { marginRight: 5 }]}/>

            <Modal transparent={true}
                   visible={this.state.modalVisible}
                   onRequestClose={() => {
                     this.setModalVisible(false);
                   }}>

              <TouchableHighlight
                  style={DatepickerStyle.datePickerMask}
                  activeOpacity={1}
                  underlayColor={'#00000077'}
                  onPress={this.onPressCancel}>
                <TouchableHighlight underlayColor={'#fff'} style={{ flex: 1 }}>
                  <Animated.View
                      style={[DatepickerStyle.datePickerCon, { height: this.state.animatedHeight }, customStyles.datePickerCon]}>

                    {this.getPicker()}

                    <TouchableHighlight
                        underlayColor={'transparent'}
                        onPress={this.onPressCancel}
                        style={[DatepickerStyle.btnText, DatepickerStyle.btnCancel, customStyles.btnCancel]}>
                      <Text
                          style={[DatepickerStyle.btnTextText, DatepickerStyle.btnTextCancel, customStyles.btnTextCancel]}>
                        {this.props.cancelBtnText}
                      </Text>
                    </TouchableHighlight>
                    <TouchableHighlight
                        underlayColor={'transparent'}
                        onPress={this.onPressConfirm}
                        style={[DatepickerStyle.btnText, DatepickerStyle.btnConfirm, customStyles.btnConfirm]}>
                      <Text
                          style={[DatepickerStyle.btnTextText, customStyles.btnTextConfirm]}>{this.props.confirmBtnText}</Text>
                    </TouchableHighlight>
                  </Animated.View>
                </TouchableHighlight>
              </TouchableHighlight>
            </Modal>


          </View>
        </TouchableHighlight>
    );
  }
}

Select.defaultProps = {
  height: 259,

  // slide animation duration time, default to 300ms, IOS only
  duration: 300,
  confirmBtnText: 'Ok',
  cancelBtnText: 'Cancel',
  customStyles: {},

  // whether or not show the icon
  showIcon: false,
  disabled: false,
  placeholder: ''
};

Select.propTypes = {
  height: React.PropTypes.number,
  duration: React.PropTypes.number,
  confirmBtnText: React.PropTypes.string,
  cancelBtnText: React.PropTypes.string,
  customStyles: React.PropTypes.object,
  showIcon: React.PropTypes.bool,
  disabled: React.PropTypes.bool,
  onValueChange: React.PropTypes.func,
  placeholder: React.PropTypes.string
};

export default Select;

import React from 'react';

const t = require('tcomb-form-native');
const Component = t.form.Component;
const Nil = t.Nil;
import TextboxField from './textbox-field';

function toNull (value) {
  return (t.String.is(value) && value.trim() === '') || Nil.is(value) ? null : value;
}

function parseNumber (value) {
  const n = parseFloat(value);
  const isNumeric = (value - n + 1) >= 0;
  return isNumeric ? n : toNull(value);
}

export default class Textbox extends Component {

  shouldComponentUpdate (nextProps, nextState) {
    const should = (
        nextState.value !== this.state.value ||
        nextState.hasError !== this.state.hasError ||
        nextProps.options !== this.props.options ||
        nextProps.type !== this.props.type
    );
    return should;
  }

  getTransformer () {
    const options = this.props.options;
    return options.transformer ? options.transformer :
        this.typeInfo.innerType === t.Number ? Textbox.numberTransformer :
            Textbox.transformer;
  }

  getTemplate () {
    return TextboxField;
  }

  getPlaceholder () {
    let placeholder = this.props.options.placeholder;
    if (Nil.is(placeholder) && this.getAuto() === 'placeholders') {
      placeholder = this.getDefaultLabel();
    }
    return placeholder;
  }

  getKeyboardType () {
    const keyboardType = this.props.options.keyboardType;
    if (t.Nil.is(keyboardType) && this.typeInfo.innerType === t.Number) {
      return 'numeric';
    }
    return keyboardType;
  }

  getLocals () {
    const locals = super.getLocals();
    locals.placeholder = this.getPlaceholder();
    locals.onChangeNative = this.props.options.onChange;
    locals.keyboardType = this.getKeyboardType();

    [
      'help',
      'autoCapitalize',
      'autoCorrect',
      'autoFocus',
      'blurOnSubmit',
      'editable',
      'maxLength',
      'multiline',
      'onBlur',
      'onEndEditing',
      'onFocus',
      'onLayout',
      'onSelectionChange',
      'onSubmitEditing',
      'placeholderTextColor',
      'secureTextEntry',
      'selectTextOnFocus',
      'selectionColor',
      'numberOfLines',
      'underlineColorAndroid',
      'clearButtonMode',
      'clearTextOnFocus',
      'enablesReturnKeyAutomatically',
      'keyboardAppearance',
      'onKeyPress',
      'returnKeyType',
      'selectionState',
      'icon',
      'iconEnd',
      'api',
      'onCallApi',
      'options'
    ].forEach((name) => locals[name] = this.props.options[name]);

    return locals;
  }

}

Textbox.transformer = {
  format: value => {
    return Nil.is(value) || value === 'null' ? '' : value
  },
  parse: toNull
};

Textbox.numberTransformer = {
  format: value => Nil.is(value) || value === 'null' ? '0' : String(value),
  parse: parseNumber
};

import React, { Component, } from 'react';
import { View, Text, TextInput, TouchableOpacity } from 'react-native';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import { IconSize, Style, Colors } from '../styles/style';
import _ from 'underscore';

function textbox (locals) {
  if (locals.hidden) {
    return null;
  }

  let stylesheet = locals.stylesheet;
  let formGroupStyle = stylesheet.formGroup.normal;
  let controlLabelStyle = stylesheet.controlLabel.normal;
  let textboxStyle = stylesheet.textbox.normal;
  let helpBlockStyle = stylesheet.helpBlock.normal;
  let errorBlockStyle = stylesheet.errorBlock;

  if (locals.hasError) {
    formGroupStyle = stylesheet.formGroup.error;
    controlLabelStyle = stylesheet.controlLabel.error;
    textboxStyle = stylesheet.textbox.error;
    helpBlockStyle = stylesheet.helpBlock.error;
  }

  if (locals.editable === false) {
    textboxStyle = stylesheet.textbox.notEditable;
  }

  const label = locals.label ? <Text style={controlLabelStyle}>{locals.label}</Text> : null;
  const help = locals.help ? <Text style={helpBlockStyle}>{locals.help}</Text> : null;
  const error = locals.hasError && locals.error ?
      <Text accessibilityLiveRegion="polite" style={errorBlockStyle}>{locals.error}</Text> : null;

  let icon = null;
  if (locals.icon) {
    if (MaterialIcons.glyphMap[locals.icon]) {
      icon = <MaterialIcons name={locals.icon} size={IconSize.medium}
                            style={[Colors.greyDarker.color(), { marginRight: 5 }]}/>
    }
    else {
      icon = <Text style={[Style.iconText, { marginRight: 5 }]}>{locals.icon.toUpperCase()}</Text>;
    }
  }

  let iconEnd = <Text style={[Style.iconText, { marginHorizontal: 15 }]}> </Text>;
  if (locals.iconEnd) {
    if (MaterialIcons.glyphMap[locals.iconEnd]) {
      iconEnd = <TouchableOpacity onPress={locals.openModal}>
        <MaterialIcons name={locals.iconEnd} size={IconSize.medium}
                       style={[Colors.greyDarker.color(), { marginHorizontal: 5 }]}/>
      </TouchableOpacity>

    }
    else {
      iconEnd = <Text
          style={[Style.iconText, {
            marginLeft: 10,
            marginRight: 15
          }]}>{locals.iconEnd.toUpperCase()}</Text>;
    }
  }

  let onFocus = locals.onFocus;

  if (locals.api === 'places') {
    iconEnd = <TouchableOpacity onPress={locals.onCallApi}>
      <MaterialIcons name='my-location' size={IconSize.medium}
                     style={[Colors.greyDarker.color(), { marginHorizontal: 5 }]}/>
    </TouchableOpacity>

  }

  else if (locals.api === 'distance-matrix') {

    const by2 = (distance) => distance * 2;

    iconEnd = <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'center' }}>

      <TouchableOpacity onPress={locals.onCallApi.bind(this, by2)}>
        <Text style={[Style.iconText, { marginRight: 5 }]}>A/R</Text>
      </TouchableOpacity>

      <TouchableOpacity onPress={locals.onCallApi}>
        <MaterialIcons name="directions" size={IconSize.medium}
                       style={[Colors.greyDarker.color(), { marginHorizontal: 5 }]}/>
      </TouchableOpacity>
    </View>

  }
  else if (locals.api === 'axesAnalytiques') {

    const _getValue = (axe) => {
      const valeurs = _.findWhere(locals.value, { idAxeAnalytique: axe.id });
      if (valeurs) {
        return valeurs;
      }
      return {};
    };

    return <View ref='input-view'>
      {
        locals.options.map((axe, index)=> {

          onFocus = locals.onCallApi.bind(this, axe);
          return <View key={`input-${index}`} style={[Style.formRow, Style.inline, formGroupStyle]}>
            {label}
            {icon}
            <TextInput
                accessibilityLabel={locals.label}
                ref={`input-${index}`}
                autoCapitalize={locals.autoCapitalize}
                autoCorrect={locals.autoCorrect}
                autoFocus={locals.autoFocus}
                blurOnSubmit={locals.blurOnSubmit}
                editable={locals.editable}
                keyboardType={locals.keyboardType}
                maxLength={locals.maxLength}
                multiline={locals.multiline}
                onBlur={locals.onBlur}
                onEndEditing={locals.onEndEditing}
                onFocus={onFocus}
                onLayout={locals.onLayout}
                onSelectionChange={locals.onSelectionChange}
                onSubmitEditing={locals.onSubmitEditing}
                placeholderTextColor={locals.placeholderTextColor}
                secureTextEntry={locals.secureTextEntry}
                selectTextOnFocus={locals.selectTextOnFocus}
                selectionColor={locals.selectionColor}
                numberOfLines={locals.numberOfLines}
                underlineColorAndroid='transparent'
                clearTextOnFocus={locals.clearTextOnFocus}
                enablesReturnKeyAutomatically={locals.enablesReturnKeyAutomatically}
                keyboardAppearance={locals.keyboardAppearance}
                onKeyPress={locals.onKeyPress}
                returnKeyType={locals.returnKeyType}
                selectionState={locals.selectionState}
                onChangeText={(value) => {
                  locals.onChange(value)
                }}
                onChange={locals.onChangeNative}
                placeholder={axe.description}
                style={[Style.input, textboxStyle]}
                value={_getValue(axe).description}
            />
            {iconEnd}
            {locals.renderModal}
            {help}
            {error}
          </View>
        })
      }
    </View>
  }

  return (
      <View style={[Style.formRow, Style.inline, formGroupStyle]}>
        {label}
        {icon}
        <TextInput
            accessibilityLabel={locals.label}
            ref="input"
            autoCapitalize={locals.autoCapitalize}
            autoCorrect={locals.autoCorrect}
            autoFocus={locals.autoFocus}
            blurOnSubmit={locals.blurOnSubmit}
            editable={locals.editable}
            keyboardType={locals.keyboardType}
            maxLength={locals.maxLength}
            multiline={locals.multiline}
            onBlur={locals.onBlur}
            onEndEditing={locals.onEndEditing}
            onFocus={locals.onFocus}
            onLayout={locals.onLayout}
            onSelectionChange={locals.onSelectionChange}
            onSubmitEditing={locals.onSubmitEditing}
            placeholderTextColor={locals.placeholderTextColor}
            secureTextEntry={locals.secureTextEntry}
            selectTextOnFocus={locals.selectTextOnFocus}
            selectionColor={locals.selectionColor}
            numberOfLines={locals.numberOfLines}
            underlineColorAndroid='transparent'
            clearButtonMode={locals.clearButtonMode}
            clearTextOnFocus={locals.clearTextOnFocus}
            enablesReturnKeyAutomatically={locals.enablesReturnKeyAutomatically}
            keyboardAppearance={locals.keyboardAppearance}
            onKeyPress={locals.onKeyPress}
            returnKeyType={locals.returnKeyType}
            selectionState={locals.selectionState}
            onChangeText={(value) => {
              locals.onChange(value)
            }}
            onChange={locals.onChangeNative}
            placeholder={locals.placeholder}
            style={[Style.input, textboxStyle]}
            value={locals.value}
        />
        {iconEnd}
        {locals.renderModal}
        {help}
        {error}
      </View>
  );
}

module.exports = textbox;

