'use strict';

import React, { Component, } from 'react';
import { Text, TouchableOpacity, InteractionManager, NetInfo, Alert, Animated } from 'react-native';
import { View } from 'react-native-animatable';

import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import Glyphicons from '../Glyphicons';
import GoogleAnalytics from 'react-native-google-analytics-bridge';

import moment from 'moment';

import { Actions, } from 'react-native-router-flux';
import { Style, IconSize, Colors } from '../styles/style';
import I18n from '../i18n/translations';

import ListEdit from '../components/list-edit-with-menu';

import CategorieDepenseService from '../services/CategorieDepenseService';
import CompteSecureService from '../services/CompteSecureService';
import DepenseService from '../services/DepenseService';
import IndemniteKilometriqueService from '../services/IndemniteKilometriqueService';
import NoteDeFraisService from '../services/NoteDeFraisService';

/**
 * Page de la liste de dépenses
 *
 * @module app/routes/DepenseCommuneListe.js
 * @override app/components/ListEdit
 */
export default class DepenseCommuneListe extends ListEdit {

  /**
   * Initialisation de l'état du composant.
   * Initialisation de l'ensemble des services utiles pour le chargement des données.
   */
  constructor () {
    super();
    /** @type {CategorieDepenseService} */
    this.categorieDepenseService = new CategorieDepenseService();
    /** @type {DepenseService} */
    this.depenseService = new DepenseService();
    /** @type {IndemniteKilometriqueService} */
    this.indemniteKilometriqueService = new IndemniteKilometriqueService();
    /** @type {NoteDeFraisService} */
    this.noteDeFraisService = new NoteDeFraisService();
    /** @type {CompteSecureService} */
    this.compteSecureService = new CompteSecureService();
    this.syncIteration = 0;
    NetInfo.addEventListener('change', this._networkListener.bind(this));
  }

  async _networkListener (reach) {
    if (this.compteSecureService.shouldUseApiService()) {
      if ((reach === 'wifi' || reach === 'cell') && this.handleError) {
        await this.onRefresh();
      }
    }
  }

  /**
   * Méthode qui retourne la liste des note de frais
   * @param props
   * @returns {array}
   */
  getElements (props) {
    const noteDeFrais = props.noteDeFrais;
    if (noteDeFrais) {
      if (props.filtre) {
        return this.noteDeFraisService[props.filtre](noteDeFrais);
      }
      return this.noteDeFraisService.findAllDepenses(noteDeFrais);
    }
    return []
  }

  /**
   * Méthode invoquée une fois que le DOM est chargé.
   */
  async componentDidMount () {
    super.componentDidMount();
    if (this.compteSecureService.shouldUseApiService()) {
      this._scheduleSync();
    }
    GoogleAnalytics.trackScreenView('DepenseCommuneListe');
  }

  async onRefresh () {
    if (this.compteSecureService.shouldUseApiService()) {
      try {
        this.handleError = false;
        return await Promise.all([
          this.noteDeFraisService.mergeAll(),
          this.depenseService.mergeAll(),
          this.indemniteKilometriqueService.mergeAll(),
        ]);
      } catch (e) {
        this.handleError = true;
      } finally {
        Actions.refresh();
      }
    }
    return Promise.resolve(true);

  }

  add () {
    Actions.categorieDepenseSelection();
  }

  onPress (depenseCommune, isSelected = false) {
    if (super.onPress(depenseCommune, isSelected)) {
      if (isSelected === false) {
        const category = this.getCategorieDepense(depenseCommune);
        if (category.id) {
          Actions.depenseForm({ depenseCommune, category });
        }
        else {
          Actions.indemniteKilometriqueForm({ depenseCommune, category });
        }
      }
    }
  }

  /**
   * Retourne la catégorie en fonction de son identifiant
   * @param idCategorieDepense
   * @returns {CategorieDepense}
   */
  getCategorieDepense ({ idCategorieDepense }) {
    return this.categorieDepenseService.find(idCategorieDepense) || this.categorieDepenseService.getIndemniteKilometriqueCategorie();
  }

  async _sync () {
    if (this.syncIteration < 2) {
      Actions.refresh();
    }
    else {
      await this.onRefresh();
    }
    this.syncIteration++;
  }

  _scheduleSync () {
    if (this.handle) {
      clearTimeout(this.handle);
    }

    if (this.syncIteration < 3) {
      this.handle = setTimeout(() => {
        requestAnimationFrame(this._sync.bind(this));
      }, 2000);
    }
  }

  _renderRowContent (depense) {
    const categorieDepense = this.getCategorieDepense(depense);

    const isSelected = this.props.selected && this.props.selected[depense.id];

    let icon = null;

    if (depense._isSynchronized === false) {

      if (this.handleError) {
        icon =
            <View animation="pulse" style={Style.imageListPreview}>
              <MaterialIcons
                  name="sync-problem"
                  size={IconSize.small}
                  style={[Colors.orange.color()]}/>
            </View>;
      }
      else {
        icon =
            <View animation="rotate" iterationCount="infinite" style={Style.imageListPreview}>
              <MaterialIcons
                  name="sync"
                  size={IconSize.small}
                  style={[Colors.yellowGreen.color()]}/>
            </View>;
        this._scheduleSync();
      }
    }
    else if (!isSelected) {
      icon = <View style={Style.imageListPreview}>
        <Glyphicons
            name={ categorieDepense.icone }
            size={IconSize.small}
            style={[Colors.greyDarker.color()]}/>
      </View>
    }

    return (
        <View style={[{ flex: 1, flexDirection: 'row' }]}>

          {icon}

          <View style={{ flex: 4 }}>
            <Text style={Style.h3}>
              {depense.description || depense.lieu}
            </Text>

            <Text style={[Style.h4, Colors.greyDarker.color()]}>
              {moment(depense.date).format('DD/MM')} - {categorieDepense.nom}
            </Text>

          </View>

          <Text style={[Style.h4, Colors.greyDarker.color()]}>
            {I18n.toCurrency(depense.montantARembourser)}
          </Text>

        </View>
    );
  }

}

'use strict';

import React, { Component, } from 'react';

import { Alert, } from 'react-native';

import _ from 'underscore';
import { Actions, } from 'react-native-router-flux';

import CompteSecureService from '../services/CompteSecureService';
import ValeurAnalytiqueService from '../services/ValeurAnalytiqueService';
import DepenseService from '../services/DepenseService';
import NoteDeFraisService from '../services/NoteDeFraisService';
import JustificatifService from '../services/JustificatifService';
import RealmForm from '../components/realm-form';
import Depense from '../schemas/Depense';
import AxeAnalytiqueService from '../services/AxeAnalytiqueService';

/**
 * Page du formulaire de depense
 *
 * @module app/routes/DepenseCommuneForm.js
 * @override app/components/RealmForm
 */
export default class DepenseCommuneForm extends RealmForm {

  constructor () {
    super();
    /** @type {AxeAnalytiqueService} */
    this.axeAnalytiqueService = new AxeAnalytiqueService();
    /** @type {CompteSecureService} */
    this.compteSecureService = new CompteSecureService();
    /** @type {ValeurAnalytiqueService} */
    this.valeurAnalytiqueService = new ValeurAnalytiqueService();
    /** @type {DepenseService} */
    this.depenseService = new DepenseService();
    /** @type {NoteDeFraisService} */
    this.noteDeFraisService = new NoteDeFraisService();
    /** @type {JustificatifService} */
    this.justificatifService = new JustificatifService();
  }

  /**
   * Retourne la dépense en cours en fonction du contexte
   * Charge les justificatifs
   * Charge les valeurs anallytiques
   * @param props
   * @returns {Object}
   */
  getStateValue (props) {
    const depenseCommune = _.clone(this.props.depenseCommune) || {};
    if (depenseCommune.justificatifs) {
      depenseCommune.justificatifs = depenseCommune.justificatifs
                                                   .map((justificatif)=> this.justificatifService.find(justificatif.id))
                                                   .filter((justificatif)=>justificatif !== null);
    }


    if (depenseCommune.valeursAnalytiques) {
      depenseCommune.valeursAnalytiques = _.toArray(depenseCommune.valeursAnalytiques)
                                                  .map((valeur)=> this.valeurAnalytiqueService.find(valeur.id));
    }

    return depenseCommune;
  }

  /**
   * Méthode qui vérifie la possibilité de supprimer un instance en fonction du contexte
   * @param props
   * @returns {boolean}
   */
  static shouldDelete (props) {
    if (props.depenseCommune) {
      return true;
    }
    return false;
  }

  /**
   * Méthode qui recalcule la TVA applicable au montant à rembourser
   * @param value
   * @returns {boolean}
   */
  onChange (value) {
    //console.log('set state ', value);
    let updatedValue = _.clone(value);
    if (this.state.value.montantARembourser != value.montantARembourser) {
      const tva = this.depenseService.computeTVA(this.props.category, updatedValue.montantARembourser);
      if (tva) {
        updatedValue.tva = tva;
      }
    }
    this.setState({ value: updatedValue });
  }

  /**
   * Retourne la configuration du formulaire
   * Charge les axes analytiques
   * @returns {Object}
   */
  getConfig () {

    if (this.compteSecureService.shouldUseApiService()) {
      const selectedAccount = this.compteSecureService.getSelectedAccount();
      const axesAnalytiques = this.axeAnalytiqueService.findAllForAccount(selectedAccount.compte.id);
      this.setState({ axesAnalytiques });

      return {
        schema: Depense.schema,
        axesAnalytiques,
        formType: 'secure'
      };
    }
    else {
      return {
        schema: Depense.schema,
        formType: 'autonome'
      };
    }

  }

  static getTitle (props) {
    return props.category.nom;
  }

  static compteSecureService = new CompteSecureService();
  static depenseService = new DepenseService();
  static noteDeFraisService = new NoteDeFraisService();

  static delete (depenseCommune, props) {
    const selectedAccount = this.compteSecureService.getSelectedAccount();
    const ndf = this.noteDeFraisService.find(this.noteDeFraisService.findEnCours(selectedAccount.compte));
    this.depenseService.delete(depenseCommune, ndf);
    this.noteDeFraisService.updateTotal(ndf);
    const noteDeFrais = this.noteDeFraisService.find(ndf.id);
    Actions.pop({ refresh: { noteDeFrais } });
  }

  static save (props, formValues) {
    const selectedAccount = this.compteSecureService.getSelectedAccount();
    const ndf = this.noteDeFraisService.find(this.noteDeFraisService.findEnCours(selectedAccount.compte));

    formValues.valeursAnalytiques = _.filter(formValues.valeursAnalytiques,   (valeur) => valeur.id)
                                     .map((valeur) => {
                                       return { id: valeur.id }
                                     });
    if (formValues.id) {
      this.depenseService.update(formValues);
      this.noteDeFraisService.updateTotal(ndf);
      const noteDeFrais = this.noteDeFraisService.find(ndf.id);
      Actions.pop({ refresh: { noteDeFrais } });
    }
    else {
      formValues.idCategorieDepense = props.category.id;
      this.depenseService.create(formValues, ndf);
      this.noteDeFraisService.updateTotal(ndf);
      const noteDeFrais = this.noteDeFraisService.find(ndf.id);
      Actions.pop({ popNum: 2, refresh: { noteDeFrais } });
    }

  }

}

