      idCompte: 'foo',
      nom: 'Tesla',
      immatriculation: 'PEACE',
      typeVehicule: 'Voiture',
      puissanceFiscale: 10
    };

    const id = vehiculeService.create(body);
    const vehicule = vehiculeService.find(id);
    assert.isNotNull(vehicule);

  });

  it('should create severals vehicules but have one "favori"', () => {

    const body = {
      idCompte: 'foo',
      nom: 'Tesla',
      immatriculation: 'PEACE',
      typeVehicule: 'Voiture',
      puissanceFiscale: 10,
      favori: true
