    const body = {
      idCompte: 'foo',
      nom: 'Tesla',
      immatriculation: 'PEACE',
      typeVehicule: 'Voiture',
      puissanceFiscale: 10,
      favori: true
    };

    for (let i = 0; i < 10; i++) {
      vehiculeService.create(body);
    }

    assert.equal(10, db.objects('Vehicule').length);
    assert.equal(1, db.objects('Vehicule').filtered('favori=true').length);

